update log_summary
SET total_records = ${text.line.count:minus(1)}
where filename = '${filename}' and ff_uuid= '${files_uuid}';

UPDATE files_ingestion
SET
	status = '${status:isNull()
						:ifElse(
							${wn_ingestion.status:notNull()
							:ifElse(
								'KO',
								${wait.counter.OK:notNull():and(${wait.counter.OK:gt(0)}):ifElse('OK','KO')}
							)},
							${status}
						)
				}',
	storage_date = ${wn_ingestion.timestamp:isNull():ifElse("null", ${wn_ingestion.timestamp:format("yyyy-MM-dd HH:mm:ss.SSS"):prepend("TIMESTAMP '"):append("'")})},
	storage_name = ${wn_ingestion.filename:isNull():ifElse("null", ${wn_ingestion.filename:prepend("'"):append("'")})}
WHERE ff_uuid = '${files_uuid}';

UPDATE zip_files_processing
SET status = 'KO'
WHERE ff_uuid = '${source.uuid}';

