e,cluster_id,cluster_name,crc_name,block_id,block_name,district_id,district_name,semester )as a left join (select * from crosstab( 'select x_axis,grade,x_value from school_grade order by 1', 'select distinct(grade) from school_grade order by 1') as (x_axis bigint,"grade_3" numeric,"grade_4" numeric,"grade_5" numeric,"grade_6" numeric ,"grade_7" numeric,"grade_8" numeric) ) as b on a.x_axis=b.x_axis

create or replace view assessment_trans_to_aggregate as select ll.school_id,ll.grade,hr.school_name, ll.school_latitude,ll.school_longitude,ll.district_id,hr.district_name,ll.district_latitude, ll.district_longitude,ll.block_id,hr.block_name,'NA' as brc_name,ll.block_latitude,ll.block_longitude,ll.cluster_id, hr.cluster_name,hr.crc_name,ll.cluster_latitude,ll.cluster_longitude,ll.semester,ll.students_count, ll.subject_1_marks_scored,ll.subject_1_total_marks, ll.subject_3_marks_scored,ll.subject_3_total_marks,ll.subject_2_marks_scored,ll.subject_2_total_marks,ll.subject_4_marks_scored,ll.subject_4_total_marks ,ll.subject_5_marks_scored,ll.subject_5_total_marks,ll.subject_7_marks_scored,ll.subject_7_total_marks,ll.subject_6_marks_scored,ll.subject_6_total_marks ,ll.subject_8_marks_scored,ll.subject_8_total_marks
from (select at.subject_1_marks_scored,at.subject_1_total_marks, at.subject_3_marks_scored,at.subject_3_total_marks,at.subject_2_marks_scored,at.subject_2_total_marks,at.subject_4_marks_scored,at.subject_4_total_marks ,at.subject_5_marks_scored,at.subject_5_total_marks,at.subject_7_marks_scored,at.subject_7_total_marks,at.subject_6_marks_scored,at.subject_6_total_marks ,at.subject_8_marks_scored,at.subject_8_total_marks,at.students_count,at.semester,at.school_id,at.grade, sg.school_latitude,sg.school_longitude,sg.district_id,sg.district_latitude,sg.district_longitude,sg.block_id,sg.block_latitude,sg.block_longitude, sg.cluster_id,sg.cluster_latitude,sg.cluster_longitude from ( select sum(subject_1_marks_scored)as subject_1_marks_scored,sum(subject_1_total_marks) as subject_1_total_marks, sum(subject_3_marks_scored)as subject_3_marks_scored,sum(subject_3_total_marks) as subject_3_total_marks, sum(subject_2_marks_scored)as subject_2_marks_scored,sum(subject_2_total_marks) as subject_2_total_marks, sum(subject_4_marks_scored)as subject_4_marks_scored,sum(subject_4_total_marks) as subject_4_total_marks, sum(subject_5_marks_scored)as subject_5_marks_scored,sum(subject_5_total_marks) as subject_5_total_marks, sum(subject_7_marks_scored)as subject_7_marks_scored,sum(subject_7_total_marks) as subject_7_total_marks, sum(subject_6_marks_scored)as subject_6_marks_scored,sum(subject_6_total_marks) as subject_6_total_marks, sum(subject_8_marks_scored)as subject_8_marks_scored,sum(subject_8_total_marks) as subject_8_total_marks ,count(distinct(student_uid)) as students_count,semester,school_id,grade from ( select sum(subject_1) as subject_1_marks_scored, sum(case when subject_1 is null then 0 else 1 end)*(case when grade between 3 and 5 then 40 when grade between 6 and 8 then 80 end) as subject_1_total_marks, sum(subject_3) as subject_3_marks_scored, sum(case when subject_3 is null then 0 else 1 end)*(case when grade between 3 and 5 then 40 when grade between 6 and 8 then 80 end) as subject_3_total_marks, sum(subject_2) as subject_2_marks_scored, sum(case when subject_2 is null then 0 else 1 end)*(case when grade between 3 and 5 then 40 when grade between 6 and 8 then 80 end) as subject_2_total_marks, sum(subject_4) as subject_4_marks_scored, sum(case when subject_4 is null then 0 else 1 end)*(case when grade between 3 and 5 then 40 when grade between 6 and 8 then 80 end) as subject_4_total_marks, sum(subject_5) as subject_5_marks_scored, sum(case when subject_5 is null then 0 else 1 end)*(case when grade between 3 and 5 then 40 when grade between 6 and 8 then 80 end) as subject_5_total_marks, sum(subject_6) as subject_6_marks_scored, sum(case when subject_6 is null then 0 else 1 end)*(case when grade between 3 and 5 then 40 when grade between 6 and 8 then 80 end) as subject_6_total_marks, sum(subject_7) as subject_7_marks_scored, sum(case when subject_7 is null then 0 else 1 end)*(case when grade between 3 and 5 then 40 when grade between 6 and 8 then 80 end) as subject_7_total_marks, sum(subject_8) as subject_8_marks_scored, sum(case when subject_8 is null then 0 else 1 end)*(case when grade between 3 and 5 then 40 when grade between 6 and 8 then 80 end) as subject_8_total_marks, student_uid,school_id,semester,grade from student_semester_trans group by student_uid,school_id,semester,grade
)as t group by school_id,semester,grade ) as at left join school_geo_master as sg on at.school_id=sg.school_id) as ll left join school_hierarchy_details as hr on ll.school_id=hr.school_id;
