OUP BY cluster_id,cluster_name,cluster_latitude,cluster_longitude,block_id,block_name,district_id,district_name,semester )as a left join (select * from crosstab( 'select x_axis,grade,x_value from cluster_grade order by 1', 'select distinct(grade) from cluster_grade order by 1') as (x_axis bigint,"grade_3" numeric,"grade_4" numeric,"grade_5" numeric,"grade_6" numeric ,"grade_7" numeric,"grade_8" numeric) ) as b on a.x_axis=b.x_axis left join ( select x_axis,sum(case when x_value <=33 then 1 else 0 end)as value_below_33, sum(case when x_value between 33 and 60 then 1 else 0 end)as value_between_33_60, sum(case when x_value between 60 and 75 then 1 else 0 end)as value_between_60_75, sum(case when x_value >=75 then 1 else 0 end)as value_above_75, round(sum(case when x_value <=33 then 1 else 0 end)*100.0/count(distinct(school_id)),2) as percent_below_33, round(sum(case when x_value between 33 and 60 then 1 else 0 end)*100.0/count(distinct(school_id)),2) as percent_between_33_60, round(sum(case when x_value between 60 and 75 then 1 else 0 end)*100.0/count(distinct(school_id)),2) as percent_between_60_75, round(sum(case when x_value >=75 then 1 else 0 end)*100.0/count(distinct(school_id)),2) as percent_above_75 from cluster_school group by x_axis)as c on b.x_axis=c.x_axis

