create or replace view district_school as SELECT district_id AS x_axis,school_id, Round(NULLIF(Sum(case when subject_1_marks_scored is null then 0 else subject_1_marks_scored end + case when subject_3_marks_scored is null then 0 else subject_3_marks_scored end+case when subject_2_marks_scored is null then 0 else subject_2_marks_scored end +case when subject_4_marks_scored is null then 0 else subject_4_marks_scored end+ case when subject_5_marks_scored is null then 0 else subject_5_marks_scored end+case when subject_7_marks_scored is null then 0 else subject_7_marks_scored end +case when subject_6_marks_scored is null then 0 else subject_6_marks_scored end+case when subject_8_marks_scored is null then 0 else subject_8_marks_scored end ),0)*100.0/ Sum(subject_1_total_marks+subject_3_total_marks+subject_2_total_marks+subject_4_total_marks+subject_5_total_marks+ subject_7_total_marks+subject_6_total_marks+subject_8_total_marks),1)AS x_value FROM school_student_subject_total_marks WHERE district_name IS NOT NULL AND block_latitude IS NOT NULL AND block_latitude <> 0 AND cluster_latitude IS NOT NULL AND cluster_latitude <> 0 and cluster_name is not null AND school_latitude <>0 AND school_latitude IS NOT NULL AND school_name IS NOT NULL and school_id not in (select school_id from school_semester_no_data) GROUP BY district_id,school_id;
select a.x_axis,district_name,x_value,y_axis,y_value,z_axis,z_value,students_count,total_schools,a.semester, b.grade_3,b.grade_4,b.grade_5,b.grade_6,b.grade_7,b.grade_8,c.value_below_33,c.value_between_33_60,c.value_between_60_75,c.value_above_75, c.percent_below_33,c.percent_between_33_60,c.percent_between_60_75,c.percent_above_75 from (SELECT district_id AS x_axis,Initcap(district_name) AS district_name, Round(Sum(case when subject_1_marks_scored is null then 0 else subject_1_marks_scored end + case when subject_3_marks_scored is null then 0 else subject_3_marks_scored end+case when subject_2_marks_scored is null then 0 else subject_2_marks_scored end +case when subject_4_marks_scored is null then 0 else subject_4_marks_scored end+ case when subject_5_marks_scored is null then 0 else subject_5_marks_scored end+case when subject_7_marks_scored is null then 0 else subject_7_marks_scored end +case when subject_6_marks_scored is null then 0 else subject_6_marks_scored end+case when subject_8_marks_scored is null then 0 else subject_8_marks_scored end )*100.0/ Sum(subject_1_total_marks+subject_3_total_marks+subject_2_total_marks+subject_4_total_marks+subject_5_total_marks+ subject_7_total_marks+subject_6_total_marks+subject_8_total_marks),1)AS x_value, 'latitude' AS y_axis,district_latitude AS y_value,'longitude' AS z_axis,district_longitude AS z_value, Sum(students_count) AS students_count,Count(DISTINCT(school_id)) AS total_schools,semester FROM school_student_subject_total_marks WHERE district_name IS NOT NULL AND block_latitude IS NOT NULL AND block_latitude <> 0 AND cluster_latitude IS NOT NULL AND cluster_latitude <> 0 AND school_latitude <>0 and cluster_name is not null AND school_latitude IS NOT NULL AND school_name IS NOT NULL and school_id not in (select school_id from school_semester_no_data) GROUP BY district_id,district_latitude,district_longitude,district_name,semester)as a left join (select * from crosstab( 'select x_axis,grade,x_value from district_grade order by 1', 'select distinct(grade) from district_grade order by 1') as (x_axis bigint,"grade_3" numeric(4,2),"grade_4" numeric(4,2),"grade_5" numeric(4,2),"grade_6" numeric(4,2) ,"grade_7" numeric(4,2),"grade_8" numeric(4,2))) b on a.x_axis=b.x_axis left join (select x_axis, sum(case when x_value <=33 then 1 else 0 end)as value_below_33, sum(case when x_value between 33 and 60 then 1 else 0 end)as value_between_33_60, sum(case when x_value between 60 and 75 then 1 else 0 end)as value_between_60_75, sum(case when x_value >=75 then 1 else 0 end)as value_above_75, round(sum(case when x_value <=33 then 1 else 0 end)*100.0/count(distinct(school_id)),2) as percent_below_33, round(sum(case when x_value between 33 and 60 then 1 else 0 end)*100.0/count(distinct(school_id)),2) as percent_between_33_60, round(sum(case when x_value between 60 and 75 then 1 else 0 end)*100.0/count(distinct(school_id)),2) as percent_between_60_75, round(sum(case when x_value >=75 then 1 else 0 end)*100.0/count(distinct(school_id)),2) as percent_above_75 from district_school group by x_axis) as c on b.x_axis=c.x_axis
